local objList = {}
Jobs:RegisterRanking('Arson', DefaultCriminal)
RegisterNetEvent('Arson.StartJob')
AddEventHandler('Arson.StartJob', function()
    local source = source
    local char = GetCharacter(source)

    if not exports['geo-inventory']:HasItemKey('Player', source, 'propane_torch') then
        TriggerClientEvent('Arson.SetStage', source, 'none')
        return
    end

    if char.id then
        if not objList[char.id] then
            objList[char.id] = Jobs.Fetch('Arson', char.id)
            if not objList[char.id] then return end
            objList[char.id].job = 'none'
        end

        if objList[char.id].job ~= 'none' then
            return
        end

        objList[char.id]:CheckPromotion()
        objList[char.id].job = 'phase_1'
        objList[char.id].jobLoc = fireLoc[Random(#fireLoc)]
        JobTime[char.id] = os.time()

        if objList[char.id]:GetHours() == 0 or GetUser(source).data.settings.jobinfo then
            TriggerClientEvent('Shared.Notif', source, [[
                Follow the directions on your GPS and light the place on fire. 
                Be careful, those torches can be pretty dangerous if you don't use them properly.
                And don't forget to come back for your payment.
            ]], 10000)
        end

        TriggerClientEvent('Arson.StartJob', source,  objList[char.id].jobLoc)
    end
end)

RegisterNetEvent('Arson.QuitJob')
AddEventHandler('Arson.QuitJob', function()
    local source = source
    local char = GetCharacter(source)

    if char.id then
        if objList[char.id] then
            objList[char.id].job = 'none'
            TriggerClientEvent('Arson.SetStage', source, 'none')
        end
    end
end)

RegisterNetEvent('Arson.CadAlert')
AddEventHandler('Arson.CadAlert', function(pos)
    local source = source
    local char = GetCharacter(source)
    if objList[char.id] and objList[char.id].job == 'phase_1' then
        local loc = vec(objList[char.id].jobLoc[1].x, objList[char.id].jobLoc[1].y, objList[char.id].jobLoc[1].z)
        exports['geo-es']:ESEvent('AddTempBlip', '10-71', loc)
        TriggerEvent('Dispatch', {
            code = '10-71A',
            title = 'Attempted Arson',
            location = pos.position,
    
            time =  os.date('%H:%M EST'),
            info = {
                {
                    icon = 'location',
                    text = pos.location,
                    location = true
                },
            }
        })
    end
end)

local fireid
RegisterNetEvent('Arson.ClearFire')
AddEventHandler('Arson.ClearFire', function()
    local source = source
    local char = GetCharacter(source)
    if char and exports['geo-es']:IsEMS(char.id) then
        TriggerClientEvent('Arson.ClearFire', -1)
        fireid = nil
    end
end)

RegisterNetEvent('Arson.Fire')
AddEventHandler('Arson.Fire', function(pos)
    local source = source
    local char = GetCharacter(source)
    if objList[char.id] and objList[char.id].job == 'phase_1' then

        if fireid then
            TriggerClientEvent('Arson.ClearFire', -1)
            fireid = nil
            Wait(100)
        end

        local id = uuid()
        fireid = id

        TriggerClientEvent('Arson.Fire', -1, objList[char.id].jobLoc)
        local loc = vec(objList[char.id].jobLoc[1].x, objList[char.id].jobLoc[1].y, objList[char.id].jobLoc[1].z)
        exports['geo-es']:ESEvent('AddTempBlip', '10-71', loc)
        TriggerEvent('Dispatch', {
            code = '10-71',
            title = 'Arson',
            location = pos.position,
    
            time =  os.date('%H:%M EST'),
            info = {
                {
                    icon = 'location',
                    text = pos.location,
                    location = true
                },
            },
            EMS = true
        })
        objList[char.id].job = 'done'
        TriggerClientEvent('Arson.SetStage', source, 'done')
        objList[char.id]:AddPayNum(Random(1000, 2500))

        Citizen.SetTimeout(1020000, function()
            if fireid == id then
                TriggerClientEvent('Arson.ClearFire', -1)
                fireid = nil
            end
        end)
    end
end)

RegisterNetEvent('Arson.Pay')
AddEventHandler('Arson.Pay', function()
    local source = source
    local char = GetCharacter(source)

    if not exports['geo-inventory']:HasItemKey('Player', source, 'propane_torch') then
        return
    end

    local item = exports['geo-inventory']:GetItemKey('Player', source, 'propane_torch')
    if item and exports['geo-inventory']:RemoveItem('Player', source, item.ID, 1) and objList[char.id] and objList[char.id].job == 'done' then
        if objList[char.id]:Pay(source, 1) then
            objList[char.id].job = 'none'
            TriggerClientEvent('Arson.SetStage', source, 'none')
        end
    end
end)

Citizen.SetTimeout(1000, function()
    TriggerClientEvent('Arson.ClearFire', -1)
    fireid = nil
end)